                    finalHTML += '
' + createVideoItem(value.title, 'http://aparat.com/v/' + value.id, value.thumbnail, value.visitcnt, value.duration, videoItemType) + '';
                });

                if ($(appendAfterThisItem).length > 0) {
                    $(finalHTML).insertAfter(appendAfterThisItem);
                } else {
                    $(notfound).remove();
                    $(wrapperSelector).append(finalHTML);
                }
            }


