                    return appendVideoItems(loadJsonMode , jsonDataResults[searchText]);
//                return jsonDataResults[searchText].forEach(createRow);
                }

                                if(!/^[A-Za-z][A-Za-z0-9 ]*$/.test(searchText)){
                    return ;
                }



                loadJsonMode = mode;
                var data_file = "https://www.aparat.com/etc/hp/search/?mode="+mode+"&word="+searchText;
                var http_request = new XMLHttpRequest();
                try{
                    // Opera 8.0+, Firefox, Chrome, Safari
                    http_request = new XMLHttpRequest();
                }catch (e){
                    // Internet Explorer Browsers
                    try{
                        http_request = new ActiveXObject("Msxml2.XMLHTTP");

                    }catch (e) {

                        try{
                            http_request = new ActiveXObject("Microsoft.XMLHTTP");
                        }catch (e){
                            // Something went wrong
//                     alert("Your browser broke!");
                            return false;
                        }

                    }
                }

                http_request.onreadystatechange = function(){

                    if (http_request.readyState == 4  ){
                        var jsonObj = JSON.parse(http_request.responseText);
//                  console.log(jsonObj);

                        if(jsonObj.data) {
                            jsonDataResults[searchText] = {};
                            jsonDataResults[searchText] = jsonObj.data;

                            if (showresponse) {

                                appendVideoItems(loadJsonMode , jsonObj.data);

//                            jsonObj.data.forEach(createRow);
                            }
                        }
                    }
                }

                http_request.open("GET", data_file, true);
                http_request.send();
            }

        (function (d) {

            'use strict';

            /*
             * The constructor of aparat video thumbnail preview sample.
             * Will add/remove the preview sample to the thumbnail on mouseenter/mouseleave.
             *
             * @param {element} elm
             * @author s.jafari@sabaidea.com
             * */

            var VideoPreview = function () {

                    this.initialize = function () {

                        this.handler();

                    };

                        /*
                         * Reads the data source and appends a video to the passed thumbnail element.
                         *
                         * @param {element} elm
                         * @author s.jafari@sabaidea.com
                         * */

                    this.add = function (elm) {

                        var src = elm.getAttribute('data-preview-src'),
                            poster = elm.style.backgroundImage.replace(/url\(|\)|"/g, ''),
                            previewElement = '
',
                            preview, video,
                            thumb = elm.querySelector('.' + thumbnailInner);

                        if (elm.hasPreview) return;

                        elm.hasPreview = true;

                        thumb.removeAttribute('title');
                        thumb.insertAdjacentHTML('afterbegin', previewElement);
                        preview = thumb.querySelector('.video-preview');
                        video = preview.querySelector('.sample-video');

                        video.onloadeddata = function () {

                            $(preview).fadeIn(300);
                            this.play();

                        };

                        video.ontimeupdate = function (e) {

                            if (Math.floor(this.currentTime) >= 5) {
                                preview.insertAdjacentHTML('beforeend', '');
                                this.pause();
                            }

                        };

                        video.onended = function () {

//                                preview.insertAdjacentHTML('beforeend', '');

                        };

                    };

                        /*
                         * Removes the preview sample from the passed thumbnail element.
                         *
                         * @param {element} elm
                         * @author s.jafari@sabaidea.com
                         * */

                    this.remove = function (elm) {

                        elm.find('.video-preview').fadeOut(300);
                        elm[0].hasPreview = false;

                        setTimeout(function () {
                            elm.find('.video-preview').remove();
                        }, 400);

                    };

                        /*
                         * Adds event handlers for adding/removing thumbnail preview sample.
                         *
                         * @author s.jafari@sabaidea.com
                         * */

                    this.handler = function () {

                        doc.ready(function () {

                            doc.on('mouseenter', '.' + thumbnail, function (e) { // Add listener

                                var target = e.target;

                                if (target.hasAttribute('data-preview-src'))
                                    this.add(target);

                                else
                                    this.add($(target).parents('[data-preview-src]')[0]);

                            }.bind(this))

                                .on('mouseleave', '.' + thumbnail, function (e) { // Remove listener

                                    var target = e.target,
                                        _this = $(target);

                                    if (_this.hasClass(thumbnail))
                                        this.remove($(target));

                                    else
                                        this.remove($(target).parents('.' + thumbnail));

                                }.bind(this))

                        }.bind(this));

                    };

                },

                preview = new VideoPreview(),
                thumbnail = 'js__thumb-preview',
                thumbnailInner = 'video-item__thumb',
                doc = $(d);

            preview.initialize();

        })(document);

